const openTimeout = Number(env.get("OPEN_TIMEOUT_SECS") || 900);
const closeTimeout = Number(env.get("CLOSE_TIMEOUT_SECS") || 900);
const abortTimeout = Number(env.get("ABORT_TIMEOUT_SECS") || 90);
const connectTimeout = Number(env.get("CONNECT_TIMEOUT_SECS") || 5);

flow.set("timeout_config", {
    open: openTimeout,
    close: closeTimeout,
    abort: abortTimeout,
    connect: connectTimeout
});

return null;

const payload = msg.payload || {};
const action = payload.action;
const timestamp = new Date().toISOString();
const telemetry = flow.get("telemetry") || {};
const derived = flow.get("derived_state") || "UNKNOWN";
const lastCommand = flow.get("last_command") || null;
const inflight = flow.get("inflight");
const timeouts = flow.get("timeout_config") || { open: 900, close: 900, abort: 90, connect: 5 };

function response(ok, state, message) {
    msg.payload = {
        ok,
        state,
        message,
        timestamp
    };
    return msg;
}

if (!action) {
    return [response(false, derived, "Missing action"), null];
}

const normalized = String(action).toLowerCase();
const allowed = ["open", "close", "abort", "status", "connect", "disconnect", "park", "unpark"];
if (!allowed.includes(normalized)) {
    return [response(false, derived, "Unsupported action"), null];
}

if (inflight) {
    return [response(false, derived, "Busy: operation in progress"), null];
}

if (normalized === "status") {
    return [response(true, derived, "Status"), null];
}

if (normalized === "disconnect") {
    return [response(true, derived, "Disconnect acknowledged"), null];
}

if (normalized === "connect") {
    flow.set("inflight", {
        action: "connect",
        deadline: Date.now() + (timeouts.connect * 1000),
        res: msg.res
    });
    const snapshotMsg = { command: "snapshot" };
    const faultClearMsg = { command: "fault_clear" };
    return [null, [snapshotMsg, faultClearMsg]];
}

const openLimit = telemetry.open_limit;
const closeLimit = telemetry.close_limit;
const moving = telemetry.moving === "1";

const targetAction = (normalized === "park") ? "close" : (normalized === "unpark" ? "open" : normalized);
const targetState = targetAction === "open" ? "OPEN" : targetAction === "close" ? "CLOSED" : null;

if (targetState === "OPEN" && derived === "OPEN") {
    return [response(true, derived, "Already open"), null];
}

if (targetState === "CLOSED" && derived === "CLOSED") {
    return [response(true, derived, "Already closed"), null];
}

if (moving) {
    if (lastCommand === targetAction) {
        return [response(true, derived, "Already in progress"), null];
    }
    if (lastCommand && lastCommand !== targetAction) {
        return [response(false, derived, "Busy: abort first"), null];
    }
    return [response(false, derived, "Busy: abort first"), null];
}

if (normalized === "abort") {
    flow.set("last_command", "abort");
    flow.set("inflight", {
        action: "abort",
        deadline: Date.now() + (timeouts.abort * 1000),
        res: msg.res
    });
    return [null, { command: "abort" }];
}

if (!targetState) {
    return [response(false, derived, "Invalid target state"), null];
}

flow.set("last_command", targetAction);
flow.set("inflight", {
    action: targetAction,
    targetState,
    deadline: Date.now() + ((targetAction === "open" ? timeouts.open : timeouts.close) * 1000),
    res: msg.res
});

return [null, { command: targetAction }];

const topicMap = {
    "Observatory/roof-esp/online": "online",
    "Observatory/roof-esp/open_limit": "open_limit",
    "Observatory/roof-esp/close_limit": "close_limit",
    "Observatory/roof-esp/moving": "moving",
    "Observatory/roof-esp/enabled": "enabled",
    "Observatory/roof-esp/fault": "fault",
    "Observatory/roof-esp/fault_code": "fault_code",
    "Observatory/roof-esp/state/open_motor": "open_motor",
    "Observatory/roof-esp/state/close_motor": "close_motor"
};

const key = topicMap[msg.topic];
if (!key) {
    return null;
}

const telemetry = flow.get("telemetry") || {};
telemetry[key] = String(msg.payload);
flow.set("telemetry", telemetry);
flow.set("last_telemetry", { ...telemetry });

const lastCommand = flow.get("last_command") || null;
const derived = deriveState(telemetry, lastCommand);
flow.set("derived_state", derived);

const inflight = flow.get("inflight");
if (!inflight || !inflight.res) {
    return null;
}

const nowIso = new Date().toISOString();

function respond(ok, message) {
    flow.set("inflight", null);
    return {
        res: inflight.res,
        payload: {
            ok,
            state: derived,
            message,
            timestamp: nowIso
        }
    };
}

if (inflight.action === "connect") {
    const online = telemetry.online === "1";
    const limitKnown = telemetry.open_limit !== undefined || telemetry.close_limit !== undefined;
    if (online && limitKnown) {
        return respond(true, "Connected");
    }
    return null;
}

if (inflight.action === "abort") {
    if (telemetry.moving === "0" && (derived === "ABORTED" || derived === "UNKNOWN")) {
        return respond(true, "Abort complete");
    }
    return null;
}

if (inflight.targetState && derived === inflight.targetState) {
    const message = inflight.targetState === "OPEN" ? "Open complete" : "Close complete";
    return respond(true, message);
}

return null;

function deriveState(telemetry, lastCommand) {
    const online = telemetry.online;
    const openLimit = telemetry.open_limit;
    const closeLimit = telemetry.close_limit;
    const moving = telemetry.moving;
    const fault = telemetry.fault;
    const faultCode = telemetry.fault_code;

    if (online === "0") {
        return "UNKNOWN";
    }

    if (fault === "1" || (faultCode && faultCode !== "0") || (openLimit === "1" && closeLimit === "1")) {
        return "FAULT";
    }

    if (moving === "1") {
        if (lastCommand === "open") {
            return "OPENING";
        }
        if (lastCommand === "close") {
            return "CLOSING";
        }
        return "UNKNOWN";
    }

    if (openLimit === "1" && closeLimit === "0") {
        return "OPEN";
    }

    if (closeLimit === "1" && openLimit === "0") {
        return "CLOSED";
    }

    if (lastCommand === "abort" && moving === "0" && openLimit === "0" && closeLimit === "0") {
        return "ABORTED";
    }

    return "UNKNOWN";
}

const inflight = flow.get("inflight");
if (!inflight || !inflight.res || !inflight.deadline) {
    return null;
}

if (Date.now() <= inflight.deadline) {
    return null;
}

const derived = flow.get("derived_state") || "UNKNOWN";
flow.set("inflight", null);

return {
    res: inflight.res,
    payload: {
        ok: false,
        state: derived,
        message: "Timeout waiting for operation",
        timestamp: new Date().toISOString()
    }
};
